document.addEventListener("DOMContentLoaded",function(){const t=()=>{document.querySelectorAll(".modal").forEach(e=>{e.style.zIndex="1050",e.querySelectorAll("input, select, textarea, button").forEach(n=>{n.style.zIndex="1051",n.style.position="relative"})}),document.querySelectorAll(".modal-backdrop").forEach(e=>{e.style.zIndex="1040"})};document.addEventListener("shown.bs.modal",t),t()});
